    onFilefieldChange: function(filefield, value, eOpts) {
        var img_reg = /\.([jJ][pP][gG]){1}$|\.([jJ][pP][eE][gG]){1}$|\.([gG][iI][fF]){1}$|\.([pP][nN][gG]){1}$|\.([bB][mM][pP]){1}$/;

        var url = 'file://'+ Ext.getCmp('file-idx').value;
        console.log(url);
        if (img_reg.test(url)) {
            if (Ext.isIE) {
                var image = Ext.getCmp('imageBrowse');
                image.src = Ext.BLANK_IMAGE_URL;// 覆盖原来的图片
                image.filters.item("DXImageTransform.Microsoft.AlphaImageLoader").src = url;
            }// 支持FF
            else {
                console.log(Ext.getCmp('file-idx').getValue());
                var file=Ext.getCmp('file-idx').getEl().down('input[type=file]').dom.files[0];
                var reader = new FileReader();
                reader.onload = function (e) {
                    // console.log($('#blah').attr('src'));
                   $('#blah').attr('src', e.target.result);
                     $('#blah').attr('width', "80%");
                 };
                 reader.readAsDataURL(file);
                Ext.getCmp('photosNameIdForm').setValue(file.name);
            }
        }
    },

    onPhotoInfo1AfterRender: function(component, eOpts) {
        //获取当前填报时间
        var registerdateField = Ext.getCmp('tbrq');
        var dateStr = "";
        var myDate = new Date();
        dateStr += myDate.getFullYear();    //获取完整的年份(4位,1970-????)
        dateStr += "-" + (myDate.getMonth()+1);       //获取当前月份(0-11,0代表1月)
        dateStr += "-" +myDate.getDate();        //获取当前日(1-31)
        registerdateField.setValue(dateStr);
    },

    onSearchKeyword_photoInfoChange1: function(field, newValue, oldValue, eOpts) {
        var getKeyword = Ext.getCmp('searchKeyword_photoInfo').getValue();
        console.log("keyword:",getKeyword);
        var mystore = Ext.StoreMgr.get('dxqy_uploadStore'); //获得store对象
        //在load事件中添加参数
        mystore.load(
            {
                params :
                {
                    searchKeyword : getKeyword
                }
            }
        );
    }

