Ext.define('MyApp.view.enterprise_uploadMapCheckResultWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.enterprise_uploadMapCheckResultWindow',

    requires: [
        'Ext.form.Panel',
        'Ext.form.field.TextArea',
        'Ext.toolbar.Toolbar',
        'Ext.toolbar.Fill',
        'Ext.button.Button'
    ],

    height: 188,
    id: 'enterprise_uploadMapCheckResultWindow',
    width: 445,
    layout: 'fit',
    title: '审核结果',
    defaultListenerScope: true,

    items: [
        {
            xtype: 'form',
            id: 'system_MapAddForm1',
            modelValidation: false,
            bodyPadding: 10,
            jsonSubmit: true,
            url: 'add_Map',
            items: [
                {
                    xtype: 'textareafield',
                    anchor: '100%',
                    height: 100,
                    id: 'ent_uploadInfoCheckRestlField',
                    fieldLabel: '审核批语',
                    labelAlign: 'right',
                    labelWidth: 80,
                    name: 'result'
                }
            ],
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    items: [
                        {
                            xtype: 'tbfill'
                        },
                        {
                            xtype: 'button',
                            text: '取消',
                            listeners: {
                                click: 'onButtonClick1'
                            }
                        },
                        {
                            xtype: 'button',
                            text: '确定',
                            listeners: {
                                click: 'onButtonClick'
                            }
                        }
                    ]
                }
            ]
        }
    ],

    onButtonClick1: function(button, e, eOpts) {
        var win  = Ext.getCmp('enterprise_uploadMapCheckResultWindow');
        win.close();
    },

    onButtonClick: function(button, e, eOpts) {
        var win = Ext.getCmp('enterprise_uploadMapCheckResultWindow');

        // 审核结果
        var checkResult = Ext.getCmp('ent_uploadInfoCheckRestlField').getValue();
        if(!checkResult || checkResult === ""){
            win.close();
            return;
        }
        //审核记录
        var records = Ext.getCmp('ent_uploadInfoCheckGrid').getSelection();
        if (records.length === 0) {
            return;
        }
        var ids = [];
        for (var re in records) {
            var record = records[re];
            ids.push(record.get('id'));
        }

        // 执行审核
        Ext.Ajax.request({
            url: 'set_EntUploadCheckResult',
            params: {
                "ids": ids,
                "checkResult": checkResult
            },
            success: function() {
                //启动窗口
                win.close();
                Ext.Msg.alert('成功', '企业信息审核完成。');
            },
            failure: function() {
                Ext.Msg.alert('失败', '审核失败，可能由网络问题造成，请刷新重试。');
            }
        });

    }

});